import React, { useState, useEffect } from "react";
import axios from "axios";

const generateTimeSlots = () => {
  const slots = [];
  for (let hour = 5; hour <= 21; hour++) {
    const amPm = hour >= 12 ? "PM" : "AM";
    const formattedHour = hour > 12 ? hour - 12 : hour;
    slots.push(`${formattedHour}:00 ${amPm}`);
    slots.push(`${formattedHour}:30 ${amPm}`);
  }
  return slots;
};

const OfficeDashboard = ({ user }) => {
  const [availableSlots, setAvailableSlots] = useState(user?.availableSlots || []);
  const [editingInfo, setEditingInfo] = useState(false);
  const [updatedInfo, setUpdatedInfo] = useState({
    name: user?.name || "",
    address: user?.address || "",
    phone: user?.phone || "",
    email: user?.email || "",
    website: user?.website || "",
  });

  const timeSlots = generateTimeSlots();

  const updateOfficeInfo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/update-office-info", {
        email: user.email,
        ...updatedInfo,
      });

      setEditingInfo(false);
      console.log("âœ… Office info updated:", response.data);
    } catch (error) {
      console.error("Error updating office info", error);
    }
  };

  const toggleSlot = async (slot) => {
    const updatedSlots = availableSlots.includes(slot)
      ? availableSlots.filter((s) => s !== slot)
      : [...availableSlots, slot];

    try {
      const response = await axios.post("http://localhost:5000/update-availability", {
        email: user.email,
        availableSlots: updatedSlots,
      });

      setAvailableSlots(response.data.office.availableSlots);
    } catch (error) {
      console.error("Failed to update slots", error);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>Welcome, {user.name}</h2>

      {/* Office Info Section */}
      <div style={{ border: "1px solid #ddd", padding: "15px", borderRadius: "10px", marginBottom: "20px" }}>
        <h3>Office Information</h3>

        {editingInfo ? (
          <>
            <input type="text" value={updatedInfo.name} onChange={(e) => setUpdatedInfo({ ...updatedInfo, name: e.target.value })} placeholder="Office Name" />
            <input type="text" value={updatedInfo.address} onChange={(e) => setUpdatedInfo({ ...updatedInfo, address: e.target.value })} placeholder="Address" />
            <input type="text" value={updatedInfo.phone} onChange={(e) => setUpdatedInfo({ ...updatedInfo, phone: e.target.value })} placeholder="Phone Number" />
            <input type="email" value={updatedInfo.email} onChange={(e) => setUpdatedInfo({ ...updatedInfo, email: e.target.value })} placeholder="Email" />
            <input type="text" value={updatedInfo.website} onChange={(e) => setUpdatedInfo({ ...updatedInfo, website: e.target.value })} placeholder="Website" />
            <button onClick={updateOfficeInfo} style={{ backgroundColor: "#007bff", color: "white", marginTop: "10px" }}>Save</button>
          </>
        ) : (
          <>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Address:</strong> {user.address}</p>
            <p><strong>Phone:</strong> {user.phone}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Website:</strong> <a href={user.website} target="_blank" rel="noopener noreferrer">{user.website}</a></p>
            <button onClick={() => setEditingInfo(true)} style={{ backgroundColor: "#28a745", color: "white", marginTop: "10px" }}>Edit Info</button>
          </>
        )}
      </div>

      {/* Time Slots Section */}
      <h3>Available Time Slots</h3>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: "10px" }}>
        {timeSlots.map((slot, index) => (
          <button
            key={index}
            onClick={() => toggleSlot(slot)}
            style={{
              padding: "8px",
              borderRadius: "5px",
              border: "none",
              cursor: "pointer",
              backgroundColor: availableSlots.includes(slot) ? "#28a745" : "#ddd",
              color: availableSlots.includes(slot) ? "white" : "black",
            }}
          >
            {slot}
          </button>
        ))}
      </div>
    </div>
  );
};

export default OfficeDashboard;
