import React, { useState, useEffect } from "react";
import axios from "axios";
import { GoogleMap, Marker, InfoWindow } from "@react-google-maps/api";

const mapContainerStyle = {
  width: "100%",
  height: "100vh",
};

const center = {
  lat: 40.7128,
  lng: -74.006,
};

const PublicMapView = () => {
  const [zipCode, setZipCode] = useState("");
  const [radius, setRadius] = useState(5);
  const [offices, setOffices] = useState([]);
  const [selectedOffice, setSelectedOffice] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchOffices();
  }, []);

  const fetchOffices = async () => {
    if (!zipCode) return;
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/search-offices?zipCode=${zipCode}&radius=${radius}`
      );
      setOffices(response.data);
    } catch (error) {
      console.error("Error fetching offices:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="map-container">
      {/* Left Sidebar */}
      <div className="sidebar">
        <h2>Find a Dentist</h2>
        <input
          type="text"
          placeholder="Enter ZIP Code"
          value={zipCode}
          onChange={(e) => setZipCode(e.target.value)}
        />
        <select value={radius} onChange={(e) => setRadius(e.target.value)}>
          <option value={1}>1 Mile</option>
          <option value={5}>5 Miles</option>
          <option value={10}>10 Miles</option>
        </select>
        <button onClick={fetchOffices}>Search</button>

        {/* Show Search Results */}
        <div className="results">
          {loading ? <p>Loading...</p> : null}
          {offices.length === 0 && !loading ? <p>No results found.</p> : null}
          {offices.map((office) => (
            <div
              key={office.id}
              className="office-card"
              onClick={() => setSelectedOffice(office)}
            >
              <h3>{office.name}</h3>
              <p>{office.zipCode}</p>
              <strong>Available Slots:</strong>
              <ul>
                {office.availableSlots.map((slot) => (
                  <li key={slot}>{slot}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>

      {/* Google Map */}
      <GoogleMap mapContainerStyle={mapContainerStyle} zoom={12} center={center}>
        {offices.map((office) => (
          <Marker
            key={office.id}
            position={office.location}
            onClick={() => setSelectedOffice(office)}
          />
        ))}
        {selectedOffice && (
          <InfoWindow
            position={selectedOffice.location}
            onCloseClick={() => setSelectedOffice(null)}
          >
            <div>
              <h2>{selectedOffice.name}</h2>
              <p>{selectedOffice.zipCode}</p>
              <strong>Available Slots:</strong>
              <ul>
                {selectedOffice.availableSlots.map((slot) => (
                  <li key={slot}>{slot}</li>
                ))}
              </ul>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </div>
  );
};

export default PublicMapView;
