import React, { useState, useEffect } from "react";
import axios from "axios";

const generateTimeSlots = () => {
  const slots = [];
  for (let hour = 5; hour <= 21; hour++) {
    const amPm = hour >= 12 ? "PM" : "AM";
    const formattedHour = hour > 12 ? hour - 12 : hour;
    slots.push(`${formattedHour}:00 ${amPm}`);
    slots.push(`${formattedHour}:30 ${amPm}`);
  }
  return slots;
};

const OfficeDashboard = ({ user, setUser }) => {
  const [availableSlots, setAvailableSlots] = useState(user?.availableSlots || []);
  const [appointments, setAppointments] = useState([]);
  const [editingInfo, setEditingInfo] = useState(false);
  const [updatedInfo, setUpdatedInfo] = useState({
    name: user?.name || "",
    address: user?.address || "",
    phone: user?.phone || "",
    email: user?.email || "",
    website: user?.website || "",
    zipCode: user?.zipCode || "",
    state: user?.state || "",
  });

  const timeSlots = generateTimeSlots();

  useEffect(() => {
    setUpdatedInfo({
      name: user?.name || "",
      address: user?.address || "",
      phone: user?.phone || "",
      email: user?.email || "",
      website: user?.website || "",
      zipCode: user?.zipCode || "",
      state: user?.state || "",
    });
  }, [user]);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/appointments?officeId=${user.id}`);
      setAppointments(response.data);
    } catch (error) {
      console.error("Error fetching appointments", error);
    }
  };

  // Reset Appointments Every 24 Hours
  useEffect(() => {
    const resetAppointments = () => setAppointments([]);
    const interval = setInterval(resetAppointments, 24 * 60 * 60 * 1000); // Every 24 hours
    return () => clearInterval(interval);
  }, []);

  const updateOfficeInfo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/update-office-info", {
        email: user.email,
        ...updatedInfo,
      });

      if (response.data.success) {
        setUser(response.data.office); // Update global user state
        setEditingInfo(false);
        console.log("✅ Office info updated:", response.data.office);
      } else {
        console.error("❌ Update failed:", response.data.message);
      }
    } catch (error) {
      console.error("Error updating office info", error);
    }
  };

  const toggleSlot = async (slot) => {
    const updatedSlots = availableSlots.includes(slot)
      ? availableSlots.filter((s) => s !== slot)
      : [...availableSlots, slot];

    try {
      const response = await axios.post("http://localhost:5000/update-availability", {
        email: user.email,
        availableSlots: updatedSlots,
      });

      setAvailableSlots(response.data.office.availableSlots);
    } catch (error) {
      console.error("Failed to update slots", error);
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", padding: "40px", marginTop: "80px" }}>
      <div style={{ display: "flex", gap: "30px" }}>
        {/* Instructions Panel (Left) */}
        <div style={{ flex: "1", borderRight: "2px solid #ddd", padding: "20px" }}>
          <h2>📌 How to Manage Your Office</h2>
          <ul style={{ lineHeight: "1.8" }}>
            <li>🔹 <strong>Update Your Profile</strong> – Edit office name, address, phone, email, and website.</li>
            <li>🔹 <strong>Set Your Availability</strong> – Click time slots to add/remove availability.</li>
            <li>🔹 <strong>View Appointments</strong> – See patient details for confirmed bookings.</li>
            <li>🔹 <strong>Automatic Reset</strong> – Appointments are cleared every 24 hours.</li>
          </ul>
        </div>

        {/* Office Profile + Availability (Center) */}
        <div style={{ flex: "2", padding: "20px" }}>
          <h2>Welcome, {user.name}</h2>

          {/* Office Info Section */}
          <div style={{ border: "1px solid #ddd", padding: "20px", borderRadius: "10px", marginBottom: "30px" }}>
            <h3>🏢 Office Information</h3>

            {editingInfo ? (
              <>
                <input type="text" value={updatedInfo.name} onChange={(e) => setUpdatedInfo({ ...updatedInfo, name: e.target.value })} placeholder="Office Name" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
                <input type="text" value={updatedInfo.address} onChange={(e) => setUpdatedInfo({ ...updatedInfo, address: e.target.value })} placeholder="Address" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
                <input type="text" value={updatedInfo.zipCode} onChange={(e) => setUpdatedInfo({ ...updatedInfo, zipCode: e.target.value })} placeholder="ZIP Code" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
                <input type="text" value={updatedInfo.state} onChange={(e) => setUpdatedInfo({ ...updatedInfo, state: e.target.value })} placeholder="State" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
                <button onClick={updateOfficeInfo} style={{ backgroundColor: "#007bff", color: "white", padding: "10px", width: "100%", borderRadius: "5px" }}>Save</button>
              </>
            ) : (
              <>
                <p><strong>Name:</strong> {user.name}</p>
                <p><strong>Address:</strong> {user.address}</p>
                <p><strong>ZIP Code:</strong> {user.zipCode}</p>
                <p><strong>State:</strong> {user.state}</p>
                <button onClick={() => setEditingInfo(true)} style={{ backgroundColor: "#28a745", color: "white", padding: "10px", width: "100%", borderRadius: "5px", marginTop: "10px" }}>Edit Info</button>
              </>
            )}
          </div>

          {/* Available Time Slots */}
          <h3>🕒 Available Time Slots</h3>
          <div style={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: "10px", marginTop: "10px" }}>
            {timeSlots.map((slot, index) => (
              <button
                key={index}
                onClick={() => toggleSlot(slot)}
                style={{
                  padding: "10px",
                  borderRadius: "5px",
                  border: "none",
                  cursor: "pointer",
                  backgroundColor: availableSlots.includes(slot) ? "#28a745" : "#ddd",
                  color: availableSlots.includes(slot) ? "white" : "black",
                }}
              >
                {slot}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default OfficeDashboard;
