import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = ({ setOffice }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(null); // Clear previous errors

        try {
            console.log("üîÑ Attempting login for:", email);
            const response = await axios.post("http://localhost:5000/login", { email, password });

            if (response.status === 200 && response.data.office) {
                console.log("‚úÖ Login successful!", response.data.office);
                setOffice(response.data.office);
                navigate("/dashboard"); // Redirect to the dashboard after login
            } else {
                throw new Error("Invalid credentials");
            }
        } catch (error) {
            console.error("‚ùå Login failed:", error);
            setError("Invalid credentials. Try again.");
        }
    };

    return (
        <div>
            <h2>Office Login</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
            <br />
            <button onClick={() => navigate("/")}>Go to Public View</button>
            <br />
            <p>
                Don't have an account? <a href="/signup">Sign Up</a>
            </p>
        </div>
    );
};

export default Login;
