import React, { useState, useEffect } from "react";
import axios from "axios";
import { GoogleMap, LoadScript, Marker, InfoWindow } from "@react-google-maps/api";

const containerStyle = {
  display: "flex",
  flexDirection: "row",
  height: "100vh",
  width: "100vw",
};

const sidebarStyle = {
  width: "30%",
  maxWidth: "400px",
  minWidth: "300px",
  padding: "20px",
  overflowY: "auto",
  backgroundColor: "#f8f9fa",
  borderRight: "2px solid #ddd",
};

const mapStyle = {
  flex: 1,
  height: "100%",
};

const PublicMapView = () => {
  const [offices, setOffices] = useState([]);
  const [selectedOffice, setSelectedOffice] = useState(null);
  const [zipCode, setZipCode] = useState("");
  const [radius, setRadius] = useState(5);
  const [mapCenter, setMapCenter] = useState({ lat: 40.7128, lng: -74.006 });
  const [bookingStatus, setBookingStatus] = useState({});

  useEffect(() => {
    fetchOffices();
  }, []);

  const fetchOffices = async () => {
    try {
      const response = await axios.get("http://localhost:5000/active-offices");
      setOffices(response.data);
    } catch (error) {
      console.error("Error fetching offices:", error);
    }
  };

  const searchOffices = async () => {
    try {
      const response = await axios.get("http://localhost:5000/search-offices", {
        params: { zipCode, radius },
      });
      setOffices(response.data);

      if (response.data.length > 0) {
        setMapCenter(response.data[0].location);
      }
    } catch (error) {
      console.error("Error searching offices:", error);
    }
  };

  const handleBooking = async (officeId, timeSlot) => {
    try {
      const response = await axios.post("http://localhost:5000/book-slot", {
        officeId,
        timeSlot,
      });

      if (response.data.success) {
        setBookingStatus((prev) => ({
          ...prev,
          [officeId]: `Successfully booked ${timeSlot}!`,
        }));

        // Remove booked slot instantly from UI
        setOffices((prevOffices) =>
          prevOffices.map((office) =>
            office.id === officeId
              ? { ...office, availableSlots: office.availableSlots.filter(slot => slot !== timeSlot) }
              : office
          )
        );
      } else {
        setBookingStatus((prev) => ({
          ...prev,
          [officeId]: "Booking failed. Try again.",
        }));
      }
    } catch (error) {
      console.error("Error booking slot:", error);
      setBookingStatus((prev) => ({
        ...prev,
        [officeId]: "Booking failed. Try again.",
      }));
    }
  };


  return (
    <div style={containerStyle}>
      {/* Sidebar */}
      <div style={sidebarStyle}>


  {/* Description */}
  <p style={{ marginTop: "100px", fontSize: "20px", color: "#555" }}>
    <h2>Find a Dentist</h2>
  </p>

  {/* Description */}
  <p style={{ marginTop: "100px", fontSize: "20px", color: "#555" }}>
    1. Enter your ZIP code and select a search radius to find nearby available dentists with open slots.
  </p>

  {/* Instructions */}
  <p style={{ fontSize: "15px", color: "#777", marginBottom: "20px" }}>
    Click on an available time slot to book an appointment instantly.
  </p>

  {/* ZIP Code Input */}
  <input
    type="text"
    placeholder="Enter ZIP Code"
    value={zipCode}
    onChange={(e) => setZipCode(e.target.value)}
    style={{ width: "100%", padding: "8px", marginBottom: "10px" }}
  />

  {/* Radius Selector */}
  <select value={radius} onChange={(e) => setRadius(e.target.value)} style={{ width: "100%", padding: "8px" }}>
    <option value={1}>1 Mile</option>
    <option value={5}>5 Miles</option>
    <option value={10}>10 Miles</option>
  </select>

  {/* Search Button */}
  <button
    onClick={searchOffices}
    style={{
      width: "100%",
      marginTop: "10px",
      padding: "10px",
      backgroundColor: "#007bff",
      color: "white",
      border: "none",
    }}
  >
    Search
  </button>

        <h4>Available Offices:</h4>
        {offices.length > 0 ? (
          offices.map((office) => (
            <div key={office.id} style={{ padding: "10px", borderBottom: "1px solid #ddd" }}>
              <strong>{office.name}</strong>
              <p>{office.city}, {office.state} {office.zipCode}</p>
              <p><strong>Available Slots:</strong></p>
              {office.availableSlots.map((slot, index) => (
                <button
                  key={index}
                  onClick={() => handleBooking(office.id, slot)}
                  disabled={bookingStatus[office.id]}
                  style={{
                    margin: "5px",
                    padding: "8px",
                    backgroundColor: bookingStatus[office.id] ? "gray" : "#28a745",
                    color: "white",
                    border: "none",
                    borderRadius: "5px",
                    cursor: bookingStatus[office.id] ? "not-allowed" : "pointer",
                  }}
                >
                  {slot}
                </button>
              ))}
              {bookingStatus[office.id] && <p>{bookingStatus[office.id]}</p>}
            </div>
          ))
        ) : (
          <p>No offices found.</p>
        )}
      </div>

      {/* Google Map */}
      <div style={mapStyle}>
        <LoadScript googleMapsApiKey="AIzaSyDGBHVURcrUdjYNhCDNjFBWawsv612pQU0">
          <GoogleMap mapContainerStyle={{ width: "100%", height: "100%" }} center={mapCenter} zoom={12}>
            {offices.map((office) =>
              office.location ? (
                <Marker key={office.id} position={office.location} onClick={() => {
                  setSelectedOffice(office);
                  setMapCenter(office.location);
                }} />
              ) : null
            )}

            {selectedOffice && (
              <InfoWindow position={selectedOffice.location} onCloseClick={() => setSelectedOffice(null)}>
                <div>
                  <h3>{selectedOffice.name}</h3>
                  <p>{selectedOffice.city}, {selectedOffice.state}, {selectedOffice.zipCode}</p>
                  <p><strong>Available Slots:</strong></p>
                  {selectedOffice.availableSlots.map((slot, index) => (
                    <button
                      key={index}
                      onClick={() => handleBooking(selectedOffice.id, slot)}
                      disabled={bookingStatus[selectedOffice.id]}
                      style={{
                        margin: "5px",
                        padding: "10px",
                        backgroundColor: bookingStatus[selectedOffice.id] ? "gray" : "#28a745",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                      }}
                    >
                      {slot}
                    </button>
                  ))}
                  {bookingStatus[selectedOffice.id] && <p>{bookingStatus[selectedOffice.id]}</p>}
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default PublicMapView;
