import React, { useState, useEffect } from "react";
import axios from "axios";

const statesList = [
  "AL", "AK", "AZ", "AR", "CA", "CO", "CT", "DE", "FL", "GA", "HI", "ID", "IL", "IN",
  "IA", "KS", "KY", "LA", "ME", "MD", "MA", "MI", "MN", "MS", "MO", "MT", "NE", "NV",
  "NH", "NJ", "NM", "NY", "NC", "ND", "OH", "OK", "OR", "PA", "RI", "SC", "SD", "TN",
  "TX", "UT", "VT", "VA", "WA", "WV", "WI", "WY"
];

const generateTimeSlots = () => {
  const slots = [];
  for (let hour = 5; hour <= 21; hour++) {
    const amPm = hour >= 12 ? "PM" : "AM";
    const formattedHour = hour > 12 ? hour - 12 : hour;
    slots.push(`${formattedHour}:00 ${amPm}`);
    slots.push(`${formattedHour}:30 ${amPm}`);
  }
  return slots;
};

const OfficeDashboard = ({ user, setUser }) => {
  const [availableSlots, setAvailableSlots] = useState(user?.availableSlots || []);
  const [appointments, setAppointments] = useState([]);
  const [editingInfo, setEditingInfo] = useState(false);
  const [updatedInfo, setUpdatedInfo] = useState({
    oldEmail: user?.email || "",
    newEmail: user?.email || "",
    name: user?.name || "",
    address: user?.address || "",
    phone: user?.phone || "",
    website: user?.website || "",
    zipCode: user?.zipCode || "",
    state: user?.state || "",
  });

  const timeSlots = generateTimeSlots();

  // üîÑ **Live Polling for Appointments**
  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/appointments?officeId=${user.id}`);
        setAppointments(response.data.length > 0 ? response.data : []);
      } catch (error) {
        console.error("Error fetching appointments", error);
      }
    };

    fetchAppointments();
    const interval = setInterval(fetchAppointments, 5000); // Refresh every 5 sec
    return () => clearInterval(interval);
  }, [user.id]);

  const updateOfficeInfo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/update-office-info", updatedInfo);

      if (response.data.success) {
        alert("‚úÖ Office info updated successfully!");
        setUser(response.data.office); // ‚úÖ Instant update
        setEditingInfo(false);
      } else {
        alert("‚ùå Update failed: " + response.data.message);
      }
    } catch (error) {
      console.error("Error updating office info", error);
      alert("‚ùå Error updating office info. Please try again.");
    }
  };

  const toggleSlot = async (slot) => {
    const updatedSlots = availableSlots.includes(slot)
      ? availableSlots.filter((s) => s !== slot)
      : [...availableSlots, slot];

    try {
      const response = await axios.post("http://localhost:5000/update-availability", {
        email: user.email,
        availableSlots: updatedSlots,
      });

      setAvailableSlots(response.data.office.availableSlots);
    } catch (error) {
      console.error("Failed to update slots", error);
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", padding: "40px", marginTop: "120px" }}>
      <div style={{ display: "flex", gap: "30px" }}>

        {/* Instructions Panel (Left) */}
        <div style={{ flex: "1", borderRight: "2px solid #ddd", padding: "20px" }}>
          <h2>üìå How to Manage Your Office</h2>
          <ul style={{ lineHeight: "1.8" }}>
            <li>üîπ <strong>Update Your Profile</strong> ‚Äì Edit office name, address, phone, email, website, and ZIP code.</li>
            <li>üîπ <strong>Set Your Availability</strong> ‚Äì Click time slots to add/remove availability.</li>
            <li>üîπ <strong>View Appointments</strong> ‚Äì See patient details for confirmed bookings.</li>
            <li>üîπ <strong>Automatic Reset</strong> ‚Äì Appointments are cleared every 24 hours.</li>
          </ul>
        </div>

        {/* Office Profile + Availability (Center) */}
        <div style={{ flex: "2", padding: "20px" }}>
          <h2>Welcome, {user.name}</h2>

          {/* Office Info Section */}
          <div style={{ border: "1px solid #ddd", padding: "20px", borderRadius: "10px", marginBottom: "30px" }}>
            <h3>üè¢ Office Information</h3>

            {editingInfo ? (
              <>
                <input type="text" value={updatedInfo.name} onChange={(e) => setUpdatedInfo({ ...updatedInfo, name: e.target.value })} placeholder="Office Name" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />

                <input type="email" value={updatedInfo.newEmail} onChange={(e) => setUpdatedInfo({ ...updatedInfo, newEmail: e.target.value })} placeholder="Office Email" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />

                <input type="text" value={updatedInfo.phone} onChange={(e) => setUpdatedInfo({ ...updatedInfo, phone: e.target.value })} placeholder="Phone Number" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />

                <input type="text" value={updatedInfo.address} onChange={(e) => setUpdatedInfo({ ...updatedInfo, address: e.target.value })} placeholder="Office Address" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />

                <input type="text" value={updatedInfo.zipCode} onChange={(e) => setUpdatedInfo({ ...updatedInfo, zipCode: e.target.value })} placeholder="ZIP Code" style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />

                <select value={updatedInfo.state} onChange={(e) => setUpdatedInfo({ ...updatedInfo, state: e.target.value })} style={{ width: "100%", padding: "10px", marginBottom: "10px" }}>
                  {statesList.map((stateCode) => (
                    <option key={stateCode} value={stateCode}>{stateCode}</option>
                  ))}
                </select>

                <button onClick={updateOfficeInfo} style={{ backgroundColor: "#007bff", color: "white", padding: "10px", width: "100%", borderRadius: "5px" }}>
                  Save
                </button>
              </>
            ) : (
              <>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Phone:</strong> {user.phone}</p>
                <p><strong>Address:</strong> {user.address}</p>
                <p><strong>ZIP Code:</strong> {user.zipCode}</p>
                <p><strong>State:</strong> {user.state}</p>

                <button onClick={() => setEditingInfo(true)} style={{ backgroundColor: "#28a745", color: "white", padding: "10px", width: "100%", borderRadius: "5px", marginTop: "10px" }}>
                  Edit Info
                </button>
              </>
            )}
          </div>

          {/* Booking Information (Right) */}
          <h2>üìã Live Appointments</h2>
          {appointments.length === 0 ? <p>No new bookings yet.</p> : appointments.map((appt, index) => (
            <div key={index} style={{ border: "1px solid #ddd", padding: "10px", marginBottom: "10px", borderRadius: "5px", backgroundColor: "#f9f9f9" }}>
              <p><strong>Patient:</strong> {appt.patientName}</p>
              <p><strong>Time:</strong> {appt.timeSlot}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OfficeDashboard;
