import React, { useEffect, useState } from "react";
import { GoogleMap, LoadScript, Marker, InfoWindow } from "@react-google-maps/api";
import axios from "axios";

const containerStyle = {
  width: "100%",
  height: "500px",
};

const defaultCenter = {
  lat: 40.7128,
  lng: -74.0060,
};

const PublicMapView = ({ loggedInOffice, navigateToDashboard }) => {
  const [offices, setOffices] = useState([]);
  const [selectedOffice, setSelectedOffice] = useState(null);

  useEffect(() => {
    fetchActiveOffices();
    const interval = setInterval(fetchActiveOffices, 3000); // Auto-refresh every 3 sec
    return () => clearInterval(interval);
  }, []);

  const fetchActiveOffices = async () => {
    console.log("üîÑ Fetching updated active offices...");
    try {
      const response = await axios.get("http://localhost:5000/active-offices");
      setOffices(response.data);
      console.log("üìç Active Offices Fetched:", response.data);
    } catch (error) {
      console.error("Error fetching active offices:", error);
    }
  };

  const handleMarkerClick = (office) => {
    setSelectedOffice(office);
  };

  const handleCloseInfoWindow = () => {
    setSelectedOffice(null);
  };

  const bookAppointment = async (office, slot) => {
    try {
      await axios.post("http://localhost:5000/book-appointment", {
        officeId: office.id,
        slot,
      });
      alert(`‚úÖ Appointment booked at ${office.name} for ${slot}`);
      fetchActiveOffices(); // Refresh availability after booking
    } catch (error) {
      console.error("Error booking appointment:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1 style={{ color: "#007bff" }}>Live Dental Office Availability</h1>

      {loggedInOffice && (
        <button
          onClick={navigateToDashboard}
          style={{
            backgroundColor: "#007bff",
            color: "white",
            padding: "10px",
            marginBottom: "10px",
            border: "none",
            cursor: "pointer",
            borderRadius: "5px",
          }}
        >
          Go to Dashboard
        </button>
      )}

      <LoadScript googleMapsApiKey="AIzaSyDGBHVURcrUdjYNhCDNjFBWawsv612pQU0">
        <GoogleMap mapContainerStyle={containerStyle} center={defaultCenter} zoom={12}>
          {offices.map((office) => {
            if (!office.location) {
              console.warn("‚ö†Ô∏è Skipping office with missing location:", office);
              return null;
            }
            return (
              <Marker key={office.id} position={office.location} onClick={() => handleMarkerClick(office)} />
            );
          })}

          {selectedOffice && (
            <InfoWindow position={selectedOffice.location} onCloseClick={handleCloseInfoWindow}>
              <div>
                <h2>{selectedOffice.name}</h2>
                <p>
                  <strong>Location:</strong> {selectedOffice.city}, {selectedOffice.state}, {selectedOffice.zipCode}
                </p>
                <h3>Available Time Slots:</h3>
                <ul>
                  {selectedOffice.availableSlots.length > 0 ? (
                    selectedOffice.availableSlots.map((slot, index) => (
                      <li key={index}>
                        {slot}{" "}
                        <button onClick={() => bookAppointment(selectedOffice, slot)}>
                          Book
                        </button>
                      </li>
                    ))
                  ) : (
                    <li>No available slots</li>
                  )}
                </ul>
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default PublicMapView;
