import React, { useEffect, useState } from 'react';
import { GoogleMap, Marker, InfoWindow } from '@react-google-maps/api';
import axios from 'axios';

const containerStyle = {
  width: '100%',
  height: '500px',
};

const center = {
  lat: 40.7128,
  lng: -74.0060,
};

const PublicMapView = () => {
  const [offices, setOffices] = useState([]);
  const [coordinates, setCoordinates] = useState({});
  const [selectedOffice, setSelectedOffice] = useState(null);

  useEffect(() => {
    fetchActiveOffices();
    const interval = setInterval(fetchActiveOffices, 1000);  // ðŸ”„ Refresh every 1 second
    return () => clearInterval(interval);
  }, []);

  const fetchActiveOffices = async () => {
    try {
      console.log("Fetching active offices...");
      const response = await axios.get('http://localhost:5000/active-offices');
      console.log("Active offices fetched:", response.data);

      const updatedOffices = await Promise.all(
        response.data.map(async (office) => {
          if (!office.location && office.zipCode) {
            console.log(`Fetching coordinates for ZIP: ${office.zipCode}`);
            const coords = await getCoordinatesFromZip(office.zipCode);
            if (coords) {
              return { ...office, location: coords };
            }
          }
          return office;
        })
      );

      setOffices(updatedOffices);
    } catch (error) {
      console.error('Error fetching active offices:', error);
    }
  };

  const getCoordinatesFromZip = async (zipCode) => {
    try {
      const response = await axios.get(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${zipCode}&key=YOUR_GOOGLE_MAPS_API_KEY`
      );
      if (response.data.results.length > 0) {
        return response.data.results[0].geometry.location;
      }
    } catch (error) {
      console.error('Error fetching coordinates for ZIP code:', error);
    }
    return null;
  };

  return (
    <div>
      <h1>Map Loading...</h1>
      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={12}>
        {offices.map((office) => {
          if (!office.location || !office.location.lat || !office.location.lng) {
            console.warn("Skipping office with missing location:", office);
            return null;
          }
          console.log("Rendering marker for:", office.name, "at", office.location);
          return (
            <Marker
              key={office.id}
              position={{ lat: office.location.lat, lng: office.location.lng }}
              onClick={() => setSelectedOffice(office)}
            />
          );
        })}
        {selectedOffice && (
          <InfoWindow
            position={selectedOffice.location}
            onCloseClick={() => setSelectedOffice(null)}
          >
            <div>
              <h2>{selectedOffice.name}</h2>
              <p>ZIP Code: {selectedOffice.zipCode}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </div>
  );
};

export default PublicMapView;
