import React, { useState } from "react";
import axios from "axios";
import "./Signup.css";

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    address: "",
    website: "",
  });

  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSignup = async () => {
    const { name, email, password, confirmPassword, phone, address, website } = formData;

    if (!name || !email || !password || !confirmPassword) {
      setError("All fields are required.");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/signup", {
        name,
        email,
        password,
        phone,
        address,
        website,
      });

      setMessage("Signup successful! You can now log in.");
      setError("");
    } catch (error) {
      setError("Email already exists or signup failed.");
    }
  };

  return (
    <div className="signup-container">
      <h2>Dental Office Sign Up</h2>
      <div className="signup-form">
        <input type="text" name="name" placeholder="Office Name" value={formData.name} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
        <input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} />
        <input type="text" name="phone" placeholder="Phone Number" value={formData.phone} onChange={handleChange} />
        <input type="text" name="address" placeholder="Office Address" value={formData.address} onChange={handleChange} />
        <input type="text" name="website" placeholder="Website (optional)" value={formData.website} onChange={handleChange} />
        <button onClick={handleSignup}>Sign Up</button>
      </div>
      {error && <p className="error-message">{error}</p>}
      {message && <p className="success-message">{message}</p>}
    </div>
  );
};

export default Signup;
