import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import PublicMapView from "./PublicMapView";
import OfficeDashboard from "./OfficeDashboard";
import Login from "./Login";
import Signup from "./SignUp";
import Navbar from "./Navbar"; // Import new Navbar component
import axios from "axios";
import "./index.css"; // Ensure your styles are correctly loaded

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [view, setView] = useState("public"); // Default to public view

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setIsLoggedIn(true);
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogin = (userData) => {
    localStorage.setItem("user", JSON.stringify(userData));
    setIsLoggedIn(true);
    setUser(userData);
    setView("dashboard");
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    setUser(null);
    setView("public");
  };

  return (
    <Router>
      <Navbar isLoggedIn={isLoggedIn} handleLogout={handleLogout} /> {/* Updated Navbar */}
      <div className="container">
        <Routes>
          <Route path="/" element={<PublicMapView />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/signup" element={<Signup />} />
          {isLoggedIn && <Route path="/dashboard" element={<OfficeDashboard user={user} />} />}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
