import React, { useState, useEffect } from "react";
import axios from "axios";
import { GoogleMap, LoadScript, Marker, InfoWindow } from "@react-google-maps/api";
import BookingForm from "./BookingForm";

const containerStyle = {
  display: "flex",
  flexDirection: "row",
  height: "100vh",
  width: "100vw",
};

const sidebarStyle = {
  width: "30%",
  maxWidth: "400px",
  minWidth: "300px",
  padding: "20px",
  overflowY: "auto",
  backgroundColor: "#f8f9fa",
  borderRight: "2px solid #ddd",
};

const mapStyle = {
  flex: 1,
  height: "100%",
};

const PublicMapView = () => {
  const [offices, setOffices] = useState([]);
  const [selectedOffice, setSelectedOffice] = useState(null);
  const [zipCode, setZipCode] = useState("");
  const [radius, setRadius] = useState(5);
  const [center, setCenter] = useState({ lat: 40.7128, lng: -74.0060 }); // Default: NYC
  const [map, setMap] = useState(null);
  const [bookingStatus, setBookingStatus] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);

  useEffect(() => {
    fetchOffices();
  }, []);

  const fetchOffices = async () => {
    try {
      const response = await axios.get("http://localhost:5000/active-offices");
      setOffices(response.data);
    } catch (error) {
      console.error("Error fetching offices:", error);
    }
  };

  const geocodeZipCode = async (zip) => {
    const apiKey = "YOUR_GOOGLE_MAPS_API_KEY"; // Replace with your actual API Key
    const geocodeUrl = `https://maps.googleapis.com/maps/api/geocode/json?address=${zip}&key=${apiKey}`;

    try {
      const response = await axios.get(geocodeUrl);
      if (response.data.results.length > 0) {
        return response.data.results[0].geometry.location; // Returns { lat, lng }
      }
    } catch (error) {
      console.error("Error fetching ZIP code location:", error);
    }
    return null;
  };

  const searchOffices = async () => {
    try {
      const response = await axios.get("http://localhost:5000/search-offices", {
        params: { zipCode, radius },
      });
      if (response.data.length > 0) {
        setOffices(response.data);
        setCenter({
          lat: response.data[0].location.lat,
          lng: response.data[0].location.lng,
        });
      } else {
        alert("No offices found in this area.");
      }
    } catch (error) {
      console.error("Error searching offices:", error);
    }
  };

  const openBookingForm = (office) => {
    setSelectedOffice(office);
    setShowBookingForm(true);
  };

  return (
    <div style={containerStyle}>
      {/* Sidebar */}
      <div style={sidebarStyle}>
        <img src="/banner.jpg" alt="Find a Dentist" style={{ width: "100%", marginBottom: "10px", marginTop: "30px" }} />
        <h2>Find an available Dentist</h2>
        <p>Enter your ZIP code and select a search radius to find nearby available dentists with open slots.</p>
        <p>Click on an available time slot to book an appointment instantly.</p>

        <input
          type="text"
          placeholder="Enter ZIP Code"
          value={zipCode}
          onChange={(e) => setZipCode(e.target.value)}
          style={{ width: "40%", padding: "8px", marginBottom: "10px" }}
        />
        <select value={radius} onChange={(e) => setRadius(e.target.value)} style={{ width: "100%", padding: "8px" }}>
          <option value={1}>1 Mile</option>
          <option value={5}>5 Miles</option>
          <option value={10}>10 Miles</option>
        </select>
        <button onClick={searchOffices} style={{ width: "100%", marginTop: "10px", padding: "10px", backgroundColor: "#007bff", color: "white", border: "none" }}>
          Search
        </button>

        <h4>Available Offices:</h4>
        {offices.length > 0 ? (
          offices.map((office) => (
            <div key={office.id} style={{ padding: "10px", borderBottom: "1px solid #ddd" }}>
              <strong>{office.name}</strong>
              <p>{office.address}</p>
              <p>{office.city}, {office.state} {office.zipCode}</p>
              <p><strong>Phone:</strong> {office.phone}</p>
              <p><strong>Email:</strong> <a href={`mailto:${office.email}`}>{office.email}</a></p>
              {office.website && (
                <p><strong>Website:</strong> <a href={office.website} target="_blank" rel="noopener noreferrer">{office.website}</a></p>
              )}
              <button onClick={() => openBookingForm(office)} style={{ marginTop: "10px", padding: "8px", backgroundColor: "#28a745", color: "white", border: "none", borderRadius: "5px" }}>
                Book Appointment
              </button>
            </div>
          ))
        ) : (
          <p>No offices found.</p>
        )}
      </div>

      {/* Google Map */}
      <div style={mapStyle}>
        <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
          <GoogleMap
            mapContainerStyle={{ width: "100%", height: "100%" }}
            center={center}
            zoom={12}
            onLoad={(mapInstance) => setMap(mapInstance)}
          >
            {offices.map((office) =>
              office.location ? (
                <Marker key={office.id} position={office.location} onClick={() => setSelectedOffice(office)} />
              ) : null
            )}

            {selectedOffice && (
              <InfoWindow position={selectedOffice.location} onCloseClick={() => setSelectedOffice(null)}>
                <div>
                  <h3>{selectedOffice.name}</h3>
                  <p>{selectedOffice.address}</p>
                  <p>{selectedOffice.city}, {selectedOffice.state}, {selectedOffice.zipCode}</p>
                  <p><strong>Phone:</strong> {selectedOffice.phone}</p>
                  <p><strong>Email:</strong> <a href={`mailto:${selectedOffice.email}`}>{selectedOffice.email}</a></p>
                  {selectedOffice.website && (
                    <p><strong>Website:</strong> <a href={selectedOffice.website} target="_blank" rel="noopener noreferrer">{selectedOffice.website}</a></p>
                  )}
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>

      {/* Booking Form (Modal) */}
      {showBookingForm && <BookingForm office={selectedOffice} onClose={() => setShowBookingForm(false)} />}
    </div>
  );
};

export default PublicMapView;
