import React, { useState } from 'react';
import axios from 'axios';

console.log("OfficeDashboard component is rendering...");

const availableHours = [
  "6:00 AM", "7:00 AM", "8:00 AM", "9:00 AM", "10:00 AM", "11:00 AM",
  "12:00 PM", "1:00 PM", "2:00 PM", "3:00 PM", "4:00 PM", "5:00 PM", "6:00 PM"
];

const OfficeDashboard = () => {
  const [officeData, setOfficeData] = useState({ email: '', password: '' });
  const [selectedSlots, setSelectedSlots] = useState([]);
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [officeInfo, setOfficeInfo] = useState(null);

  const handleInputChange = (e) => {
    setOfficeData({ ...officeData, [e.target.name]: e.target.value });
  };

  const handleUpdateAvailability = async () => {
    try {
      console.log("Updating availability for:", officeData.email, "Slots:", selectedSlots);
      await axios.post('http://localhost:5000/update-availability', {
        email: officeData.email,
        availableSlots: selectedSlots,
      });
      console.log("Availability updated successfully!");
      setMessage('Availability updated successfully!');
    } catch (error) {
      console.error('Error updating availability:', error);
      setMessage('Failed to update availability. Try again.');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log("Attempting login for:", officeData.email);
      const response = await axios.post('http://localhost:5000/login-office', officeData);
      setIsLoggedIn(true);
      setOfficeInfo(response.data);
      setSelectedSlots(response.data.availableSlots || []);
      setMessage('Login successful!');
      console.log("Login successful for:", response.data);
    } catch (error) {
      console.error('Error logging in:', error);
      setMessage('Invalid credentials. Try again.');
    }
  };

  return (
    <div>
      <h2>Dental Office Dashboard</h2>
      {!isLoggedIn ? (
        <form onSubmit={handleLogin}>
          <input type="email" name="email" placeholder="Email" value={officeData.email} onChange={handleInputChange} required />
          <input type="password" name="password" placeholder="Password" value={officeData.password} onChange={handleInputChange} required />
          <button type="submit">Login</button>
        </form>
      ) : (
        <div>
          <h3>Welcome, {officeInfo?.name}</h3>
          <p>Location: {officeInfo?.city}, {officeInfo?.state}, {officeInfo?.zipCode}</p>
          <h3>Select Available Hours for Today</h3>
          <div>
            {availableHours.map(hour => (
              <button
                key={hour}
                onClick={() => console.log("Toggling slot:", hour)}
                style={{ margin: '5px', padding: '10px', backgroundColor: selectedSlots.includes(hour) ? 'green' : 'gray', color: 'white', border: 'none', cursor: 'pointer' }}
              >
                {hour}
              </button>
            ))}
          </div>
        </div>
      )}
      {message && <p>{message}</p>}
    </div>
  );
};

export default OfficeDashboard;
